package com.arrays;

public class EvenOddArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           int []a= {5,7,8,4,2,9,6};//e=4,o=3
           int e=0,o=0;
           for(int i=0;i<a.length;i++)
           {
        	   if(a[i]%2==0)
        		   e++;
        	   else
        		   o++;
           }
           System.out.println(e+" "+o);
        //   int []b= {5,4,9,9,4,7,6,3};//u=5
        //   int[]bb= {1,1,1,};//u=1
           //array sort
	}
	

}
